import java.util.Arrays;
import java.util.HashMap;

public class IsSubsequence {

    public class Pair {
        int index;
        int[] arr;

        public Pair(int index, int[] arr){
            this.index = index;
            this.arr = arr;
        }
        
    }
    public static void main(String[] args){
        System.out.println(new IsSubsequence().isSubsequence("leeeeetcode", ""));
    }

    public boolean isSubsequence(String s, String t) {
        HashMap<Character,Pair> map = new HashMap<>();
        for (int i = 0; i < t.length(); i++) {
            if(map.containsKey(t.charAt(i))){
                int[] arr = map.get(t.charAt(i)).arr;
                int index = map.get(t.charAt(i)).index;
                index++;
                arr[index] = i;
                map.put(t.charAt(i), new Pair(index,arr));

            }else{
                int arr[] = new int[t.length()];
                Arrays.fill(arr, -1);
                arr[0]=i;
                map.put(t.charAt(i), new Pair(0,arr));

            }
        }
        int previousIndex=-1;
        for (int i = 0; i < s.length(); i++) {
            if(map.containsKey(s.charAt(i))){
                int[] arr = map.get(s.charAt(i)).arr;
                int temp = previousIndex;
                for (int j = 0; j < arr.length; j++) {
                    if(arr[j]==-1){
                        break;
                    }
                    if(previousIndex ==-1 || previousIndex < arr[j]){
                        previousIndex = arr[j];
                        break;
                    }
                }
                if(temp == previousIndex){
                    return false;
                }
                
            }else{
                return false;
            }
        }
        return true;
    }
}
